import React,{Fragment,useContext} from 'react';
import Tarea from './Tarea';
import proyectoContext from '../../context/proyectos/proyectoContext';


const ListadoTareas = () => {
    const proyectosContext = useContext(proyectoContext);
    const {proyecto, eliminarProyecto} = proyectosContext;
    //si no hay proyecto selecionado 
    if(!proyecto) return <h2>Seleciona una Sala</h2>
    //Array destructuring para extraer el proyecto actual

    const [proyectoActual] = proyecto;

    const tareasProyecto = [
        {nombre: 'Elegir Plataforma', estado: true},
        {nombre: 'Elegir Colores', estado: true},
        {nombre: 'Elegir pago', estado: true},
        {nombre: 'dormir', estado: false}
    ];
    // Elimina un proyecto
    const onClickEliminar = () =>{
        eliminarProyecto(proyectoActual.id)
    }
    return (
        <Fragment>
        <h2>Sala: {proyectoActual.nombre}</h2>
        <ul className="listado-tareas">
            {tareasProyecto.length === 0
             ?(<li className ="tarea"><p>No Hay Tareas</p></li>)            
             : tareasProyecto.map(tarea => (
                 <Tarea 
                    tarea={tarea}
                 />
             ))
            }


        </ul>
        <button 
        type="button"
        className="btn btn-eliminar"
        onClick = {onClickEliminar}
        >Eliminar Sala &times;</button>
        </Fragment>
     );
}
 
export default ListadoTareas;
