import React, {useReducer} from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {TAREAS_PROYECTO,AGREGAR_TAREA,
        VALIDAR_TAREA,ELIMINAR_TAREA,
        ESTADO_TAREA

} from '../../types';
const TareaState = props => {

    const initialState = {
        tareas: [
            {id:1, nombre: 'Director', estado: true, proyectoId: 1},
            {id:2,nombre: 'Camara', estado: true, proyectoId: 2},
            {id:3,nombre: 'Tom', estado: true, proyectoId: 3},
            {id:4,nombre: 'Asistente', estado: false, proyectoId: 4}
        ],
        tareasproyecto: null,
        errortarea: false
    }


    //crear dispatch y state

    const [state, dispatch] = useReducer(TareaReducer, initialState);
    // Crear las Funciones

    //Obtener las tareas de un proyecto

    const obtenerTareas = proyectoId => {
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    }
    //agregar una tarea al proyecto
    const agregarTarea = tarea => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    }

    //valida y muesta un error
    const validarTarea = () => {
        dispatch({
            type: VALIDAR_TAREA
        })
    }
    //eliminar tarea por id 
    const eliminarTarea = id => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: id
        })
    }
    //cambia el estado de cada tarea
    const cambiarEstadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }
    
    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto : state.tareasproyecto,
                errortarea: state.errortarea,
                obtenerTareas,
                agregarTarea,
                validarTarea,
                eliminarTarea,
                cambiarEstadoTarea
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
